sql.content:
select id, parent_id, code from bpm_re_proctype where 


	
		(code=#{code} or id=#{parentId, dataType=number, length=10})
	
	
		code=#{code} 
	



	and tenant_id = #{tenantId}

select count(id) from bpm_re_proctype where parent_id =#{id, dataType=number, length=10}
union all
select count(id) from bpm_re_procdef where type_id =#{id, dataType=number, length=10}
union all
select parent_id from bpm_re_proctype where id=#{id, dataType=number, length=10}
