sql.content:
with delegations as ( select user_id, assigned_user_id, reason, procdef_code, procdef_version, accept_time from bpm_re_delegation p left join bpm_re_delegation_detail s on (p.id = s.delegation_id) where p.is_enabled=1 and p.start_time <= #{currentTime, dataType=number, length=13} and p.end_time >= #{currentTime, dataType=number, length=13} and
#{userId}
) select * from delegations where procdef_code is null union select * from delegations where procdef_code= #{procdefCode} and procdef_version is null union select * from delegations where procdef_code= #{procdefCode} and procdef_version= #{procdefVersion} order by accept_time desc
